<?php

require_once "./vendor/autoload.php";
class boevenDatabaseSetup
{

    private $conn;


    public function __construct()
    {
        //laad .env
        $dotenv = Dotenv\Dotenv::createImmutable(__DIR__ . '/');
        $dotenv->load();

        $host = $_ENV['Host'];
        $username = $_ENV['Username'];
        $password = $_ENV['Password'];
        $db = $_ENV['Db'];
        //maak verbinding met mysql
        $this->conn = new mysqli($host, $username, $password);
        //foutafhandeling bij connectie
        if ($this->conn->connect_error) {
            die("error" . $this->conn->connect_error);
        }
        //als de db nog niet bestaat maak deze aan en selecteert deze
        $sql = "CREATE DATABASE IF NOT EXISTS $db ";
        $this->conn->query($sql);
        $this->conn->select_db($db);
    }

    public function createTables()
    {

        $sql = "CREATE TABLE IF NOT EXISTS Verdachte (
            verdachte_id INT NOT NULL,
            naam VARCHAR(50) NOT NULL,
            geslacht VARCHAR(5) NOT NULL,
            leeftijd INT NOT NULL,
            lengte VARCHAR(9) NOT NULL,
            haarkleur VARCHAR(5) NOT NULL,
            kleur_ogen VARCHAR(5) NOT NULL,
            gezichtsbeharing BIT NOT NULL,
            tatoeages BIT NOT NULL,
            bril VARCHAR(3) NOT NULL,
            littekens BIT,
            schoenmaat VARCHAR(9) NOT NULL,
            PRIMARY KEY(verdachte_id)
        )";
        if (!$this->conn->query($sql)) {
            echo "error" . $this->conn->error . "<br>";
        }

        //maak misdaad tabel aan
        $sql = "CREATE TABLE IF NOT EXISTS Misdaad (
            misdaad_id INT NOT NULL,
            verdachte_id INT NOT NULL,
            misdaad_type VARCHAR(45) NOT NULL,
            datum_gepleegd DATE NOT NULL,
            gevangenis VARCHAR(45) NOT NULL,
            gedrag VARCHAR(45) DEFAULT NULL,
            start_datum DATE NOT NULL,
            eind_datum DATE,
            PRIMARY KEY (misdaad_id)
        )";
        if (!$this->conn->query($sql)) {
            echo "error " . $this->conn->error . "<br>";
        }

        //maak gebruiker tabel aan
        $sql = "CREATE TABLE IF NOT EXISTS Gebruiker (
            Gebruiker_id INT AUTO_INCREMENT,
            naam TEXT,
            Tijd INT DEFAULT 0,
            Geld INT DEFAULT 0,
            Klas VARCHAR(10),
            PRIMARY KEY(Gebruiker_id)
        )";
        if (!$this->conn->query($sql)) {
            echo "error " . $this->conn->error . "<br>";
        }

        echo "succesvol aangemaakt <br>";
    }

    public function insertData()
    {
        //voert alle verdachten inserts uit
        $this->insertVerdachten();
        //voert alle misdaad inserts uit
        $this->insertMisdaden();
    }

