    //sluit db verbinding
    public function __destruct()
    {
        // als de databaseverbinding bestaat, sluit deze en reset de variabel.
        if (isset($this->conn)) {
            $this->conn->close(); // sluit database verbinding
            $this->conn = null; // voorkomt verdere queries
        }
    }
